def nb_withnumber
    # Combien d'handle contiennent un numéro ?
    nb = 0
    $journalists.each do |journalist|
        compteur = 0
        "123456789".each_char do |number|
            if (journalist.include? number) == true
                compteur = 1
            end
        end
        nb += compteur
    end
    print "Le nombre de journaliste avec un nombre est #{nb}"
    puts
end

